class Fruit {
    id: string
    type: string
    imageUrl: string
    price: number
    description: string

    constructor(type: string, imageUrl: string, price: number, description: string) {
        this.id = `${Math.random()}`;
        this.type = type;
        this.imageUrl = imageUrl;
        this.price = price;
        this.description = description;
    }
}

const mango = new Fruit("Mango", "data:image/jpeg;base64,", 18.90, "Exotic and juicy, the best tropical fruit out there!");
const banana = new Fruit("Banana", "data:image/jpeg;base64,", 7.80, "Great for snacking or baking.");
const watermelon = new Fruit("Watermelon", "data:image/jpeg;base64,", 4.90, "Sweet, refreshing and incredibly juicy. Perfect for summer!");
console.log(mango);
console.log(banana);
console.log(watermelon);